package javabasic;

public class Elseif {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=10;
		int b=10;
		int c=10;
		
		if(a==b)
		{
			System.out.println(a);
		}
		else if (a>b)
		{
			System.out.println(b);
			
		}
		else if(a<b)
		{
			System.out.println(a);
			System.out.println("pandurang");
			 
		}
		
		else if (a==c)
		{
			System.out.println(b);
			System.out.println("deepika");
		}

	}

}
