package javabasic;

public class Operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=20;
		int b=10;
		int c=a+b;
		int d=a-b;
		int e=a*b;
		int f=a/b;
		System.out.println(c);
		System.out.println(d);
		System.out.println(e);
		System.out.println(f);

	}

}
